SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET AUTOCOMMIT = 0;
START TRANSACTION;
SET time_zone = "+00:00";
CREATE DATABASE IF NOT EXISTS `jili_changyangyang` DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci;
USE `jili_changyangyang`;

CREATE TABLE `banner` (
  `id` int(11) NOT NULL,
  `description` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `img_url` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


CREATE TABLE `cars` (
  `id` int(11) NOT NULL COMMENT '主键',
  `car_name` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '汽车名称',
  `sale` int(255) NOT NULL DEFAULT 0 COMMENT '汽车销量',
  `shop_index_img` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `head_img_url` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'head显示的汽车图片',
  `car_size` varchar(10) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '外形尺寸',
  `car_engine` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '发动机',
  `car_wheelbase` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '轴距',
  `car_trans_case` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '变速箱',
  `car_km_fuel_consumption` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '百公里耗油',
  `offical_price` varchar(10) COLLATE utf8_unicode_ci NOT NULL COMMENT '官方价格',
  `cate_id` int(11) DEFAULT NULL COMMENT '分类ID',
  `stock` int(255) DEFAULT NULL COMMENT '库存'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


CREATE TABLE `cars_volume_base` (
  `id` int(11) NOT NULL,
  `car_id` int(11) NOT NULL,
  `volume_id` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


CREATE TABLE `category` (
  `id` int(11) NOT NULL,
  `cate_name` varchar(255) COLLATE utf8_unicode_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


CREATE TABLE `city` (
  `id` int(11) NOT NULL DEFAULT 0,
  `pid` int(11) DEFAULT NULL COMMENT '父级ID',
  `cityname` varchar(255) CHARACTER SET utf8 DEFAULT NULL COMMENT '城市名',
  `type` int(11) DEFAULT NULL COMMENT '省市识别码'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


CREATE TABLE `colors` (
  `id` int(11) NOT NULL,
  `car_id` int(5) NOT NULL COMMENT '汽车ID',
  `color_name` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '颜色名称',
  `sm_url` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '小图片背景',
  `img_url` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '汽车图片'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


CREATE TABLE `inner_color` (
  `id` int(11) NOT NULL,
  `car_id` int(11) DEFAULT NULL COMMENT '汽车ID',
  `sm_url` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '小背景图',
  `img_url` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '内饰图片',
  `title` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '内饰标题',
  `description` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '内饰描述',
  `col_name` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '内饰颜色名称'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


CREATE TABLE `model` (
  `id` int(11) NOT NULL,
  `model_name` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '型号名称'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


CREATE TABLE `model_volume_base` (
  `id` int(11) NOT NULL,
  `model_id` int(11) NOT NULL,
  `volume_id` int(11) DEFAULT NULL,
  `car_id` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


CREATE TABLE `news` (
  `id` int(11) NOT NULL,
  `title` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `description` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '描述',
  `content` varchar(20000) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '内容',
  `bowser_count` int(255) DEFAULT 0 COMMENT '浏览量',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `mod_time` datetime DEFAULT NULL COMMENT '修改时间',
  `like_count` int(255) DEFAULT 0 COMMENT '喜欢统计',
  `news_pic` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '新闻封面图片',
  `share_count` int(255) DEFAULT 0 COMMENT '分享统计'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


CREATE TABLE `price` (
  `id` int(11) NOT NULL,
  `car_price` int(10) DEFAULT NULL COMMENT '汽车价格',
  `car_id` int(11) DEFAULT NULL COMMENT '对应的汽车ID',
  `volume_id` int(11) DEFAULT NULL COMMENT '对应的排量ID',
  `model_id` int(11) DEFAULT NULL COMMENT '对应的型号ID'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


CREATE TABLE `stock` (
  `id` int(11) NOT NULL,
  `car_id` int(11) NOT NULL COMMENT '汽车ID',
  `volume_id` int(11) DEFAULT NULL COMMENT '排量ID',
  `model_id` int(11) DEFAULT NULL COMMENT '型号ID',
  `color_id` int(11) DEFAULT NULL COMMENT '颜色ID',
  `stock` int(255) DEFAULT NULL COMMENT '库存数量'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


CREATE TABLE `users` (
  `id` int(255) NOT NULL,
  `username` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '用户名',
  `password` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '密码',
  `reg_time` date DEFAULT NULL COMMENT '注册时间',
  `name` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '姓名',
  `birthday` date DEFAULT NULL COMMENT '出生日期',
  `occupation` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '职业',
  `location` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '所在地',
  `email` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '邮箱',
  `per_sig` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '个性签名',
  `sex` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '性别',
  `state` int(255) DEFAULT 1 COMMENT '用户状态',
  `avatar` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '用户头像'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


CREATE TABLE `volume` (
  `id` int(11) NOT NULL,
  `name` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '排量名称',
  `car_id` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;



